import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { forkJoin, map, Observable, switchMap } from 'rxjs';
import { Employee, Presence } from 'src/app/models/presencemodel';

@Injectable({
  providedIn: 'root'
})
export class PresenceService {

  private apiUrl = 'http://localhost:8082/api/attendances';
  private employeeApiUrl = 'http://localhost:8082/api/employees'; // URL des employés

  constructor(private http: HttpClient) {}

  getAllPresences(): Observable<Presence[]> {
    return this.http.get<Presence[]>(this.apiUrl + '/all').pipe(
      switchMap(presences => {
        // Pour chaque présence, récupérez les détails de l'employé
        const observables = presences.map(p => {
          return this.http.get<Employee>(`${this.employeeApiUrl}/${p.employeeId}`).pipe(
            map(employee => ({
              ...p,
              employeeName: employee.name // Ajoutez le nom de l'employé
            }))
          );
        });
        return forkJoin(observables);
      })
    );
  }

  getPresenceById(id: string): Observable<Presence> {
    return this.http.get<Presence>(`${this.apiUrl}/${id}`);
  }

  savePresence(presence: Presence): Observable<Presence> {
    return this.http.post<Presence>(`${this.apiUrl}/saveAttendance`, presence);
  }

  updatePresence(id: string, presence: Presence): Observable<Presence> {
    return this.http.put<Presence>(`${this.apiUrl}/${id}`, presence);
  }

  deletePresence(id: string): Observable<void> {
    return this.http.delete<void>(`${this.apiUrl}/${id}`);
  }

}
