import { Component } from '@angular/core';
import { RequestService } from 'src/app/core/services/request.service';
import { Request } from 'src/app/models/presencemodel';

@Component({
  selector: 'app-requestadmin',
  templateUrl: './requestadmin.component.html',
  styleUrls: ['./requestadmin.component.css']
})
export class RequestadminComponent {

  requests: Request[] = [];

  constructor(private requestService: RequestService) {}

  ngOnInit() {
    this.requestService.getAllRequests().subscribe(data => this.requests = data);
    this.getAllRequests();
  }

  /*updateStatus(id: string, status: string) {
    this.requestService.updateStatus(id, status).subscribe(() => {
      this.ngOnInit(); // refresh
    });
  }*/

  updateStatus(req: Request, newStatus: 'APPROVED' | 'REJECTED') {
    const updatedRequest = { ...req, status: newStatus };

    this.requestService.updateStatus(updatedRequest).subscribe({
      next: (res) => {
        req.status = newStatus; // mise à jour locale
      },
      error: (err) => {
        console.error('Erreur lors de la mise à jour :', err);
      }
    });
  }



  getAllRequests() {
    this.requestService.getAllRequests().subscribe((data: Request[]) => {
      this.requests = data;
    });
  }

}
