import { HttpClient } from '@angular/common/http';
import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-add-presence',
  templateUrl: './add-presence.component.html',
  styleUrls: ['./add-presence.component.css']
})
export class AddPresenceComponent {
  presence = {
    employeeId: '',
    date: '',
    checkInTime: '',
    checkOutTime: ''
  };

  successMessage = '';

  constructor(private http: HttpClient) {}

  onSubmit() {
    this.http.post('http://localhost:8082/api/attendances/saveAttendance', this.presence)
      .subscribe({
        next: response => {
          this.successMessage = 'Présence enregistrée avec succès !';
          this.presence = { employeeId: '', date: '', checkInTime: '', checkOutTime: '' };
        },
        error: error => {
          console.error('Erreur lors de l\'enregistrement :', error);
        }
      });
  }
}
