import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { RequestService } from 'src/app/core/services/request.service';

@Component({
  selector: 'app-myrequests',
  templateUrl: './myrequests.component.html',
  styleUrls: ['./myrequests.component.css']
})
export class MyrequestsComponent implements OnInit {

  requests: any[] = [];

  constructor(
    private route: ActivatedRoute,
    private requestService: RequestService,
    private router: Router
  ) {}

  ngOnInit() {
    this.route.queryParams.subscribe((params: any) => {
      const empId = params['employeeId'];
      console.log('Employee ID:', empId); // Affiche l'ID de l'employé

      if (empId) {
        this.requestService.getRequestsByEmployee(empId).subscribe(data => {
          console.log('Requests:', data); // Affiche les données récupérées
          this.requests = data;

          // Vérification des IDs dans les données récupérées
          this.requests.forEach(request => {
            console.log('Request ID:', request._id); // Affiche l'ID de chaque requête
          });
        });
      }
    });
  }




  editRequest(id: string) {
    console.log('Request ID:', id); // Vérifie si l'ID est bien passé
    if (id) {
      this.router.navigate(['edit-request', id]);
    } else {
      console.error('Request ID is undefined');
    }
  }




}
