import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { Presence } from 'src/app/models/presencemodel';
import { PresenceService } from 'src/app/core/services/presence.service';


@Component({
  selector: 'app-presence-list',
  templateUrl: './presence-list.component.html',
  styleUrls: ['./presence-list.component.css']
})
export class PresenceListComponent implements OnInit{

  presences: Presence[] = [];

  constructor(private presenceService: PresenceService, private router: Router) {}

  ngOnInit(): void {
    this.loadPresences();
  }

  loadPresences(): void {
    this.presenceService.getAllPresences().subscribe({
        next: (data) => {
            this.presences = data;
            console.log('Présences chargées:', data); // Pour debug
        },
        error: (err) => console.error('Erreur de chargement des présences', err)
    });
}

  onEdit(presence: Presence): void {
    this.router.navigate(['/admin/attendances/edit', presence.id]);
  }

  onDelete(id: string): void {
    this.presenceService.deletePresence(id).subscribe({
      next: () => this.loadPresences(),
      error: (err) => console.error('Erreur suppression présence', err)
    });
  }


}


<app-admin-header></app-admin-header>
<app-admin-sidebar></app-admin-sidebar>

<main class="main-wrapper">
  <div class="container-fluid">
    <div class="inner-contents">
      <div class="page-header d-flex align-items-center justify-content-between mr-bottom-30">
        <div class="left-part">
          <h2 class="text-dark">Liste des Présences</h2>
          <p class="text-gray mb-0">Historique des présences des employés.</p>
        </div>
      </div>

      <div class="card border-0 p-5">
        <div class="card-header bg-transparent border-0 p-0 d-flex align-items-center justify-content-between gap-3">
          <h6 class="mb-0">Détails des Présences</h6>
        </div>

        <div class="card-body p-0 pt-4">
          <table class="table table-striped">
            <thead>
              <tr>
                <th>Employé</th>
                <th>Date</th>
                <th>Entrée</th>
                <th>Sortie</th>
                <th>Heures Travaillées</th>
                <th>Heures Supplémentaires</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              <tr *ngFor="let presence of presences">
                <td>
                  <small class="text-muted d-block">ID: {{ presence.employeeId || 'Non disponible' }}</small>

                </td>
                <td>{{ (presence.date | date:'dd/MM/yyyy') || 'N/A' }}</td>
                <td>{{ presence.checkInTime || 'N/A' }}</td>
                <td>{{ presence.checkOutTime || 'N/A' }}</td>
                <td>{{ presence.hoursWorked?.toFixed(2) || '0.00' }}</td>
                <td>{{ presence.overtimeHours?.toFixed(2) || '0.00' }}</td>
                <td>
                  <button class="btn btn-warning" (click)="onEdit(presence)">Modifier</button>
                  <button class="btn btn-danger" (click)="onDelete(presence.id!)">Supprimer</button>
                </td>
              </tr>
            </tbody>
          </table>
        </div>
      </div>
    </div>
  </div>
</main>

<app-admin-footer></app-admin-footer>
