import { Component } from '@angular/core';
import { Validators } from '@angular/forms';
import { RequestService } from 'src/app/core/services/request.service';

@Component({
  selector: 'app-requestform',
  templateUrl: './requestform.component.html',
  styleUrls: ['./requestform.component.css']
})
export class RequestformComponent {

  form = this.fb.group({
    type: ['', Validators.required],
    employeeId: ['', Validators.required],
    startDate: ['', Validators.required],
    endDate: ['', Validators.required],
    justification: ['']
  });
  router: any;




  constructor(private requestService: RequestService) {}

  /*
  submitRequest() {
    this.requestService.createRequest(this.request).subscribe(response => {
      console.log('Demande soumise avec succès', response);
    });
  }
    */


  submitRequest() {
    if (this.request.employeeId && this.request.type && this.request.startDate && this.request.endDate) {
      this.requestService.createRequest(this.request).subscribe(() => {
        this.router.navigate(['/user/myrequests'], {
          queryParams: { employeeId: this.request.employeeId }
        });
      });
    } else {
      alert("Merci de remplir tous les champs requis.");
    }
  }



}
