import { HttpClient } from '@angular/common/http';
import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-add-presence',
  templateUrl: './add-presence.component.html',
  styleUrls: ['./add-presence.component.css']
})
export class AddPresenceComponent implements OnInit{
  presence = {
    employeeId: '',
    date: '',
    checkInTime: '',
    checkOutTime: ''
  };

  successMessage = '';

  constructor(private http: HttpClient) {}

  ngOnInit(): void {
    // Pré-remplir la date avec aujourd'hui
    const today = new Date();
    this.presence.date = today.toISOString().split('T')[0];

    // Heure actuelle (pré-remplissage facultatif)
    const currentTime = today.toTimeString().split(':').slice(0, 2).join(':');
    this.presence.checkInTime = currentTime;
    this.presence.checkOutTime = currentTime;

    // Charger employeeId depuis le localStorage
    const storedEmployeeId = localStorage.getItem('employeeId');
    if (storedEmployeeId) {
      this.presence.employeeId = storedEmployeeId;
    }
  }

  onSubmit() {
    this.http.post('http://localhost:8082/api/attendances', this.presence)
      .subscribe({
        next: () => {
          this.successMessage = 'Présence enregistrée avec succès !';
          this.resetForm();
        },
        error: error => {
          console.error('Erreur lors de l\'enregistrement :', error);
        }
      });
  }

  resetForm() {
    const today = new Date();
    const currentTime = today.toTimeString().split(':').slice(0, 2).join(':');
    this.presence = {
      employeeId: localStorage.getItem('employeeId') || '',
      date: today.toISOString().split('T')[0],
      checkInTime: currentTime,
      checkOutTime: currentTime
    };
  }
}
