import { Component } from '@angular/core';
import { ActivatedRoute } from '@angular/router';

@Component({
  selector: 'app-editrequests',
  templateUrl: './editrequests.component.html',
  styleUrls: ['./editrequests.component.css']
})
export class EditrequestsComponent {

  requestId: string | null = null;  // Permet à requestId de prendre aussi la valeur null

  constructor(private route: ActivatedRoute) {}

  ngOnInit() {
    this.route.paramMap.subscribe(params => {
      const id = params.get('id');
      if (id) {
        this.requestId = id;
        console.log(this.requestId); // Vérifie si l'ID est bien récupéré
      } else {
        console.error('ID not found');
      }
    });
  }
  ngOnInit() {
    this.route.queryParams.subscribe((params: any) => {
      const empId = params['employeeId'];
      if (empId) {
        this.requestService.getRequestsByEmployee(empId).subscribe(data => {
          console.log('Requests:', data); // Affiche les données récupérées
          this.requests = data;
        });
      }
    });
  }

}
