import { Component } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { EmployeeService } from 'src/app/core/services/employee.service';
import { PresenceService } from 'src/app/core/services/presence.service';
import { Presence } from 'src/app/models/presencemodel';

@Component({
  selector: 'app-presence-form',
  templateUrl: './presence-form.component.html',
  styleUrls: ['./presence-form.component.css']
})
export class PresenceFormComponent {

  presence = {
    employeeId: '',
    date: '',
    checkInTime: '',
    checkOutTime: ''
  };

  message: string = '';

  constructor(private presenceService: PresenceService, private router: Router) { }

  onSubmit(): void {
    // Crée un objet presence à partir des valeurs du formulaire
    const presenceData: Presence = {
      employeeId: this.presence.employeeId,
      date: this.presence.date,
      checkInTime: this.presence.checkInTime,
      checkOutTime: this.presence.checkOutTime
    };

    this.presenceService.savePresence(presenceData).subscribe(
      response => {
        this.message = 'Fiche de paie ajoutée avec succès !';
        setTimeout(() => {
          this.router.navigate(['/presences']); // Redirige vers la liste des fiches de paie après ajout
        }, 2000);
      },
      error => {
        console.error('Erreur lors de l’ajout de la fiche de paie', error);
      }
    );
  }

}
<app-admin-header></app-admin-header>
<app-admin-sidebar></app-admin-sidebar>

<!-- Main Wrapper -->
<main class="main-wrapper">
  <div class="container-fluid">
    <div class="inner-contents">
      <div class="page-header d-flex align-items-center justify-content-between mr-bottom-30">
        <div class="left-part">
          <h2 class="text-dark">Ajouter une Fiche de Paie</h2>
          <p class="text-gray mb-0">Veuillez remplir les informations ci-dessous pour ajouter une nouvelle fiche de paie.</p>
        </div>
        <div class="right-part">
          <form class="search-form w-auto" action="https://wpthemebooster.com/demo/themeforest/html/kleon/search.php">
            <input type="text" name="search" class="bg-white form-control" placeholder="Search">
            <button type="submit" class="btn"><img src="assets/img/svg/search.svg" alt=""></button>
          </form>
        </div>
      </div>

      <!-- Formulaire d'ajout -->
      <div class="card border-0 p-5">
        <div class="card-header bg-transparent border-0 p-0 d-flex align-items-center justify-content-between gap-3">
          <h6 class="mb-0">Détails de la Fiche de Paie</h6>
        </div>

        <div class="card-body p-0 pt-4">
          <form (ngSubmit)="onSubmit()" #payrollForm="ngForm">
            <div class="form-group">
              <label class="form-label" for="employeeId">Employé</label>
              <input type="text" class="form-control" id="employeeId" [(ngModel)]="payroll.employeeId" name="employeeId" placeholder="ID de l'employé" required />
            </div>

            <div class="form-group">
              <label class="form-label" for="salary">Salaire</label>
              <input type="number" class="form-control" id="salary" [(ngModel)]="payroll.salary" name="salary" placeholder="Salaire" required />
            </div>

            <div class="form-group">
              <label class="form-label" for="bonus">Bonus</label>
              <input type="number" class="form-control" id="bonus" [(ngModel)]="payroll.bonus" name="bonus" placeholder="Bonus" />
            </div>

            <div class="form-group">
              <label class="form-label" for="payDate">Date de Paiement</label>
              <input type="date" class="form-control" id="payDate" [(ngModel)]="payroll.payDate" name="payDate" required />
            </div>

            <div class="button_group">
              <button type="submit" class="btn btn-primary" [disabled]="payrollForm.invalid">Ajouter la Fiche de Paie</button>

            </div>
          </form>
        </div>
      </div>

    </div>
  </div>
</main>

<app-admin-footer></app-admin-footer>
