import { HttpClient } from '@angular/common/http';
import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-add-presence',
  templateUrl: './add-presence.component.html',
  styleUrls: ['./add-presence.component.css']
})
export class AddPresenceComponent implements OnInit {
  presence = {
    employeeId: '',
    date: '',
    checkInTime: '',
    checkOutTime: ''
  };

  checkInMessage = '';
  checkOutMessage = '';
  errorMessage = '';

  constructor(private http: HttpClient) {}

  ngOnInit(): void {
    this.presence.date = this.getTodayDate();
  }

  getTodayDate(): string {
    const today = new Date();
    return today.toISOString().split('T')[0];
  }

  getCurrentTime(): string {
    const now = new Date();
    return now.toTimeString().slice(0, 5);
  }

  checkIn(): void {
    const { employeeId, date } = this.presence;

    // Vérifie si une présence existe déjà
    this.http.get<any>(`http://localhost:8082/api/attendances/byEmployeeAndDate/${employeeId}/${date}`)
      .subscribe({
        next: existing => {
          if (existing && existing.checkInTime) {
            this.errorMessage = `⚠️ Check-in déjà effectué à ${existing.checkInTime}`;
            this.clearMessagesAfterDelay();
          } else {
            this.presence.checkInTime = this.getCurrentTime();
            this.presence.checkOutTime = '';
            this.http.post('http://localhost:8082/api/attendances/saveAttendance', this.presence)
              .subscribe({
                next: response => {
                  this.checkInMessage = `✅ Check-in effectué à ${this.presence.checkInTime}`;
                  this.presence.checkOutTime = '';
                  this.clearMessagesAfterDelay();
                },
                error: error => {
                  console.error('Erreur check-in:', error);
                  this.errorMessage = "❌ Erreur lors de l'enregistrement du check-in.";
                  this.clearMessagesAfterDelay();
                }
              });
          }
        },
        error: () => {
          // Si l'absence signifie pas encore enregistrée (normal)
          this.presence.checkInTime = this.getCurrentTime();
          this.presence.checkOutTime = '';
          this.http.post('http://localhost:8082/api/attendances/saveAttendance', this.presence)
            .subscribe({
              next: response => {
                this.checkInMessage = `✅ Check-in effectué à ${this.presence.checkInTime}`;
                this.clearMessagesAfterDelay();
              },
              error: error => {
                console.error('Erreur check-in:', error);
                this.errorMessage = "❌ Erreur lors de l'enregistrement du check-in.";
                this.clearMessagesAfterDelay();
              }
            });
        }
      });
  }

  checkOut(): void {
    const { employeeId, date } = this.presence;

    // Vérifie s'il y a un check-in avant de faire un check-out
    this.http.get<any>(`http://localhost:8082/api/attendances/byEmployeeAndDate/${employeeId}/${date}`)
      .subscribe({
        next: existing => {
          if (!existing || !existing.checkInTime) {
            this.errorMessage = "⚠️ Impossible de faire un check-out sans check-in préalable.";
            this.clearMessagesAfterDelay();
          } else if (existing.checkOutTime) {
            this.errorMessage = `⚠️ Check-out déjà effectué à ${existing.checkOutTime}`;
            this.clearMessagesAfterDelay();
          } else {
            const checkOutTime = this.getCurrentTime();
            this.http.put('http://localhost:8082/api/attendances/updateCheckOutTime', {
              employeeId,
              date,
              checkOutTime
            }).subscribe({
              next: () => {
                this.presence.checkOutTime = checkOutTime;
                this.checkOutMessage = `✅ Check-out effectué à ${checkOutTime}`;
                this.clearMessagesAfterDelay();
              },
              error: () => {
                this.errorMessage = "❌ Erreur lors du check-out.";
                this.clearMessagesAfterDelay();
              }
            });
          }
        },
        error: () => {
          this.errorMessage = "❌ Erreur lors de la vérification de la présence.";
          this.clearMessagesAfterDelay();
        }
      });
  }

  clearMessagesAfterDelay(): void {
    setTimeout(() => {
      this.checkInMessage = '';
      this.checkOutMessage = '';
      this.errorMessage = '';
    }, 5000);
  }
}
