import { Component } from '@angular/core';

@Component({
  selector: 'app-editrequests',
  templateUrl: './editrequests.component.html',
  styleUrls: ['./editrequests.component.css']
})
export class EditrequestsComponent {

  requestId: string;

  constructor(private route: ActivatedRoute) {}

  ngOnInit() {
    this.route.paramMap.subscribe(params => {
      this.requestId = params.get('id');
      console.log(this.requestId); // Vérifie si l'ID est bien récupéré
    });
  }

}
