import { Component } from '@angular/core';
import { RequestService } from 'src/app/core/services/request.service';

@Component({
  selector: 'app-requestadmin',
  templateUrl: './requestadmin.component.html',
  styleUrls: ['./requestadmin.component.css']
})
export class RequestadminComponent {

  requests: Request[] = [];

  constructor(private requestService: RequestService) {}

  ngOnInit() {
    this.requestService.getAllRequests().subscribe(data => this.requests = data);
  }

  updateStatus(id: string, status: string) {
    this.requestService.updateStatus(id, status).subscribe(() => {
      this.ngOnInit(); // refresh
    });
  }



}
