import { Component } from '@angular/core';
import { FormBuilder, Validators } from '@angular/forms';
import { Router } from '@angular/router';
import { RequestService } from 'src/app/core/services/request.service';

@Component({
  selector: 'app-requestform',
  templateUrl: './requestform.component.html',
  styleUrls: ['./requestform.component.css']
})
export class RequestformComponent {

  form = this.fb.group({
    type: ['', Validators.required],
    employeeId: ['', Validators.required],
    startDate: ['', Validators.required],
    endDate: ['', Validators.required],
    justification: ['']
  });

  constructor(
    private fb: FormBuilder,
    private requestService: RequestService,
    private router: Router
  ) {

  }

  submitRequest() {
    if (this.form.valid) {
      if (this.requestId) {
        // Mode édition
        this.requestService.updateRequest(this.requestId, this.form.value).subscribe(() => {
          this.router.navigate(['/user/myrequests'], {
            queryParams: { employeeId: this.form.value.employeeId }
          });
        });
      } else {
        // Nouvelle requête
        this.requestService.createRequest(this.form.value).subscribe(() => {
          this.router.navigate(['/user/myrequests'], {
            queryParams: { employeeId: this.form.value.employeeId }
          });
        });
      }
    } else {
      alert("Merci de remplir tous les champs requis.");
    }
  }




}
<main class="main">
  <section class="section-box">
    <div class="box-head-single">
      <div class="container">
        <h3>Leave Request Form</h3>
        <ul class="breadcrumbs">
          <li><a href="#">Home</a></li>
          <li>Leave Request</li>
        </ul>
      </div>
    </div>
  </section>

  <!-- Formulaire stylisé -->
  <div class="card-body pt-3">
    <form [formGroup]="form" (ngSubmit)="submitRequest()">
      <div class="row">
        <div class="col-lg-6">
          <div class="form-group">
            <label class="form-label">Leave Type</label>
            <select class="form-control" formControlName="type" required>
              <option value="" disabled>Select leave type</option>
              <option value="Sick Leave">Sick Leave</option>
              <option value="Paid Leave">Paid Leave</option>
              <option value="Unpaid Leave">Unpaid Leave</option>
            </select>
          </div>
        </div>

        <div class="col-lg-6">
          <div class="form-group">
            <label class="form-label">Employee ID</label>
            <input
              type="text"
              class="form-control"
              formControlName="employeeId"
              placeholder="Enter employee ID"
              required
            />
          </div>
        </div>

        <div class="col-lg-6">
          <div class="form-group">
            <label class="form-label">Start Date</label>
            <input
              type="date"
              class="form-control"
              formControlName="startDate"
              required
            />
          </div>
        </div>

        <div class="col-lg-6">
          <div class="form-group">
            <label class="form-label">End Date</label>
            <input
              type="date"
              class="form-control"
              formControlName="endDate"
              required
            />
          </div>
        </div>

        <div class="col-lg-12">
          <div class="form-group">
            <label class="form-label">Justification</label>
            <textarea
              class="form-control"
              formControlName="justification"
              placeholder="Enter justification"
              rows="3"
            ></textarea>
          </div>
        </div>
      </div>

      <div class="form-group">
        <button type="submit" class="btn btn-primary" [disabled]="form.invalid">
          Submit Request
        </button>
      </div>
    </form>
  </div>
</main>
