import { HttpClient } from '@angular/common/http';
import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-add-presence',
  templateUrl: './add-presence.component.html',
  styleUrls: ['./add-presence.component.css']
})
export class AddPresenceComponent implements OnInit {
  presence = {
    employeeId: '',
    date: '',
    checkInTime: '',
    checkOutTime: ''
  };

  successMessage = '';

  constructor(private http: HttpClient) {}

  ngOnInit(): void {
    this.presence.date = this.getTodayDate();
  }

  getTodayDate(): string {
    const today = new Date();
    return today.toISOString().split('T')[0]; // format: YYYY-MM-DD
  }

  getCurrentTime(): string {
    const now = new Date();
    return now.toTimeString().slice(0, 5); // format: HH:mm
  }

  setCheckInNow(): void {
    this.presence.checkInTime = this.getCurrentTime();
  }

  setCheckOutNow(): void {
    this.presence.checkOutTime = this.getCurrentTime();
  }

  onSubmit(): void {
    this.http.post('http://localhost:8082/api/attendances/saveAttendance', this.presence)
      .subscribe({
        next: response => {
          this.successMessage = 'Présence enregistrée avec succès !';
          // Réinitialiser avec date du jour + heures vides
          this.presence = {
            employeeId: '',
            date: this.getTodayDate(),
            checkInTime: '',
            checkOutTime: ''
          };
        },
        error: error => {
          console.error('Erreur lors de l\'enregistrement :', error);
        }
      });
  }
}
